function validateGame(e){for(var t=document.getElementById(e+"columns").value,n=document.getElementById(e+"rows").value,a=document.getElementById(e+"moves").value,u=document.getElementsByName(e+"turn"),m=[t,n,a],r=["number of columns","number of rows","moves"],l=0;l<m.length;l++)if(null===m[l]||""===m[l])return alert("Game must specify "+r[l]),!1;return t=parseInt(t,10),n=parseInt(n,10),4>t||t>15?(alert("Must have between 4 and 15 columns."),!1):4>n||n>15?(alert("Must have between 4 and 15 rows."),!1):u[0].checked||u[1].checked?!0:(alert("Please pick whether you want to go first or second."),!1)}function validate_create(){var e="newgame_",t=document.getElementById(e+"columns").value,n=document.getElementById(e+"rows").value,a=document.getElementsByName(e+"turn"),u=validateGame(e);return u?(turn=a[0].checked?1:2,create_game(t,n,turn),!1):!1}