function validateGame(e){for(var t=document.getElementById(e+"columns").value,n=document.getElementById(e+"rows").value,a=document.getElementById(e+"moves").value,u=[t,n,a],m=["number of columns","number of rows","moves"],l=0;l<u.length;l++)if(null==u[l]||""==u[l])return alert("Game must specify "+m[l]),!1;return t=parseInt(t),n=parseInt(n),4>t||t>15?(alert("Must have between 4 and 15 columns."),!1):4>n||n>15?(alert("Must have between 4 and 15 rows."),!1):!0}function validate_create(){var e="newgame_",t=document.getElementById(e+"columns").value,n=document.getElementById(e+"rows").value,a=validateGame(e);return a?(create_game(t,n),!1):!1}